    10→
    11→export class ComboSystem {
    12→  private combo: number = 0;
    13→  private comboTimer: number = 0;
    14→  private multiplier: number = 1.0;
    15→  private maxCombo: number = 0;
    16→
    17→  /**
    18→   * Increment combo count and reset timer
    19→   */
    20→  increment(): void {
    21→    this.combo++;
    22→    this.comboTimer = COMBO_TIMEOUT * 1000; // Convert to ms
    23→    this.updateMultiplier();
    24→    
    25→    // Track max combo
    26→    if (this.combo > this.maxCombo) {
    27→      this.maxCombo = this.combo;
    28→    }
    29→    
    30→    // Emit combo updated event
    31→    EventBus.emit('combo-updated', {
    32→      count: this.combo,
    33→      multiplier: this.multiplier,
    34→    });
    35→  }
    36→
    37→  /**
    38→   * Reset combo to 0
    39→   */
    40→  reset(): void {
    41→    this.combo = 0;
    42→    this.comboTimer = 0;
    43→    this.multiplier = 1.0;
    44→    
    45→    // Emit combo updated event
    46→    EventBus.emit('combo-updated', {
    47→      count: this.combo,
    48→      multiplier: this.multiplier,
    49→    });
    50→  }
    51→
    52→  /**
    53→   * Update combo timer and reset if expired
    54→   * @param time - Current time
    55→   * @param delta - Time since last update (ms)
    56→   */
    57→  update(time: number, delta: number): void {
    58→    if (this.combo > 0) {
    59→      this.comboTimer -= delta;
    60→      
    61→      if (this.comboTimer <= 0) {
    62→        this.reset();
    63→      }
    64→    }
    65→  }
    66→
    67→  /**
    68→   * Update multiplier based on current combo
    69→   */
    70→  private updateMultiplier(): void {
    71→    this.multiplier = 1 + (this.combo * COMBO_MULTIPLIER_RATE);
    72→  }
    73→
    74→  /**
    75→   * Get current combo count
    76→   */
    77→  getCombo(): number {
    78→    return this.combo;
    79→  }
