   192→  private calculateMonsterScore(monster: Monster): {
   193→    finalScore: number;
   194→    finalSouls: number;
   195→    isCritical: boolean;
   196→  } {
   197→    const basePoints = monster.getPoints();
   198→    let multiplier = 1.0;
