     2→ * PowerUpManager
     3→ *
     4→ * Manages active power-ups and their effects.
     5→ * Singleton pattern for global access.
     6→ */
     7→
     8→import Phaser from 'phaser';
     9→import { PowerUpType, UpgradeId } from '@config/types';
    10→import { POWERUP_SLOW_MOTION, POWERUP_FRENZY, POWERUP_SHIELD, POWERUP_SOUL_MAGNET } from '@config/constants';
    11→import { EventBus } from '@utils/EventBus';
    12→import { UpgradeManager } from './UpgradeManager';
    13→
    14→export class PowerUpManager {
    15→  private static instance: PowerUpManager | null = null;
    16→  private activePowerUps: Map<PowerUpType, number> = new Map();
    17→  private scene: Phaser.Scene | null = null;
    18→  private shieldInstance: any = null; // Reference to active ShieldPowerUp
    19→  private upgradeManager: UpgradeManager | null = null;
    20→
    21→  private constructor() {}
    22→
    23→  /**
    24→   * Get singleton instance
    25→   */
    26→  static getInstance(): PowerUpManager {
    27→    if (!PowerUpManager.instance) {
    28→      PowerUpManager.instance = new PowerUpManager();
    29→    }
    30→    return PowerUpManager.instance;
    31→  }
    32→
    33→  /**
    34→   * Initialize the manager with a scene reference
    35→   */
    36→  initialize(scene: Phaser.Scene): void {
    37→    this.scene = scene;
    38→  }
    39→
    40→  /**
    41→   * Set upgrade manager reference
    42→   */
    43→  setUpgradeManager(upgradeManager: UpgradeManager): void {
    44→    this.upgradeManager = upgradeManager;
    45→  }
    46→
    47→  /**
    48→   * Activate a power-up
    49→   * @param type - Type of power-up to activate
    50→   */
    51→  activatePowerUp(type: PowerUpType): void {
    52→    const duration = this.getDuration(type);
    53→    const expirationTime = Date.now() + duration * 1000;
    54→    
    55→    this.activePowerUps.set(type, expirationTime);
    56→    
    57→    // Store reference to shield power-up for consumption
    58→    if (type === PowerUpType.SHIELD) {
    59→      // Find the shield power-up instance in the scene
    60→      const powerUpObj = this.scene?.children.getByName('powerup');
    61→      if (powerUpObj) {
    62→        const powerUp = powerUpObj as any;
    63→        if (powerUp.getPowerUpType && powerUp.getPowerUpType() === PowerUpType.SHIELD) {
    64→          this.shieldInstance = powerUp;
    65→        }
    66→      }
    67→    }
    68→  }
    69→
    70→  /**
    71→   * Deactivate a power-up
    72→   * @param type - Type of power-up to deactivate
    73→   */
    74→  deactivatePowerUp(type: PowerUpType): void {
    75→    this.activePowerUps.delete(type);
    76→    
    77→    // Clear shield reference
    78→    if (type === PowerUpType.SHIELD) {
    79→      this.shieldInstance = null;
    80→    }
    81→  }
    82→
    83→  /**
    84→   * Consume the shield (called when villager is sliced)
    85→   */
    86→  consumeShield(): void {
    87→    if (this.shieldInstance && this.activePowerUps.has(PowerUpType.SHIELD)) {
    88→      this.shieldInstance.consume();
    89→      this.deactivatePowerUp(PowerUpType.SHIELD);
    90→    }
    91→  }
    92→
    93→  /**
    94→   * Update active power-ups and check for expiration
    95→   * @param time - Current time
    96→   * @param delta - Time since last update
    97→   */
    98→  update(time: number, delta: number): void {
    99→    const currentTime = Date.now();
   100→    const expiredTypes: PowerUpType[] = [];
   101→    
   102→    // Check for expired power-ups
   103→    this.activePowerUps.forEach((expirationTime, type) => {
   104→      if (currentTime >= expirationTime) {
   105→        expiredTypes.push(type);
   106→      }
   107→    });
   108→    
   109→    // Deactivate expired power-ups
   110→    expiredTypes.forEach((type) => {
   111→      this.deactivatePowerUp(type);
   112→    });
   113→  }
   114→
   115→  /**
   116→   * Check if a power-up is currently active
   117→   * @param type - Type of power-up to check
   118→   */
   119→  isActive(type: PowerUpType): boolean {
   120→    return this.activePowerUps.has(type);
   121→  }
   122→
   123→  /**
   124→   * Get remaining time for a power-up in seconds
   125→   * @param type - Type of power-up to check
   126→   */
   127→  getRemainingTime(type: PowerUpType): number {
   128→    const expirationTime = this.activePowerUps.get(type);
   129→    if (!expirationTime) return 0;
   130→    
   131→    const remaining = Math.max(0, (expirationTime - Date.now()) / 1000);
   132→    return remaining;
   133→  }
   134→
   135→  /**
   136→   * Get the duration for a power-up type
   137→   * @param type - Type of power-up
   138→   */
   139→  private getDuration(type: PowerUpType): number {
   140→    switch (type) {
   141→      case PowerUpType.SLOW_MOTION:
   142→        // Apply upgrade bonus to slow motion duration
   143→        let duration: number = POWERUP_SLOW_MOTION.duration;
   144→        if (this.upgradeManager) {
   145→          const bonus = this.upgradeManager.getUpgradeValue(UpgradeId.SLOW_MOTION_DURATION);
   146→          duration = bonus;
   147→        }
   148→        return duration;
   149→      case PowerUpType.FRENZY:
   150→        return POWERUP_FRENZY.duration;
   151→      case PowerUpType.SHIELD:
   152→        return POWERUP_SHIELD.duration;
   153→      case PowerUpType.SOUL_MAGNET:
   154→        return POWERUP_SOUL_MAGNET.duration;
   155→      default:
   156→        return 0;
   157→    }
   158→  }
   159→
   160→  /**
   161→   * Get all active power-up types
   162→   */
   163→  getActivePowerUps(): PowerUpType[] {
   164→    return Array.from(this.activePowerUps.keys());
   165→  }
   166→
   167→  /**
   168→   * Reset all power-ups (call when starting new game)
   169→   */
   170→  reset(): void {
   171→    this.activePowerUps.clear();
   172→    this.shieldInstance = null;
   173→  }
   174→
   175→  /**
   176→   * Check if frenzy is active (for score calculation)
   177→   */
   178→  isFrenzyActive(): boolean {
   179→    return this.isActive(PowerUpType.FRENZY);
   180→  }
   181→
   182→  /**
   183→   * Check if soul magnet is active (for soul calculation)
   184→   */
   185→  isSoulMagnetActive(): boolean {
   186→    return this.isActive(PowerUpType.SOUL_MAGNET);
   187→  }
   188→
   189→  /**
   190→   * Check if shield is active (for villager penalty)
   191→   */
   192→  isShieldActive(): boolean {
   193→    return this.isActive(PowerUpType.SHIELD);
   194→  }
   195→}
